import { device } from '../devices'
const Header = () => (
    <header >
        <div className="shape"></div>
         <div className="logoWrapper">
            <img className="logoArt" src="/images/logo-art.png"></img>
            <h1 className="title">ANIME TRAILERS</h1>
        </div>
        <style jsx>{`


            header {
                display: flex;
                justify-content: flex-end;
                width: 100%;
                position:absolute;
                top:0;
                right: 0;
            }

            .clipShape {
                background-color: #E6E6E6;
                flex: 0 0 5%;
                min-height: 150px;

            }

            .logoWrapper {
                background-color: #E6E6E6;
                flex: 0 0 56%;
                min-height: 150px;
                text-align: right;
                clip-path: polygon(50% 0%, 100% 0, 100% 100%, 19% 100%, 0 81%, 0 0);

            }
            .logoArt{
                height: 110px;
                margin: 15px 15px 0 0;

            }


            .title {
                font-family: council, sans-serif;
                font-weight: 400;
                font-style: normal;
                text-align: center;
                font-size: 1.8em;
                text-align: center;
                width: 80%;
                margin: 0 10px 5px 0px;
                display: inline-block;
            }

            @media ${device.tablet} {

            }
        `}
        </style>
    </header>
)

export default Header
