import React, { useContext, useEffect, useRef} from 'react'
import { AnimeContext } from '../contexts/AnimeContext'
import { OverlayContext } from '../contexts/OverlayContext'
const Footer = () =>  {

    const { selected } = useContext(AnimeContext)
    const { dispatch } = useContext(OverlayContext)
    const buttonRef = useRef() 
    const showOverlay = () => {
        if(!Object.keys(selected).length == 0) {
            dispatch({type:'HIDE_SHOW_OVERLAY', boolean:false})
        }
    }

    useEffect(() => {
    },[selected])

    return( 
        <div className="footer">
            <div ref={buttonRef} className={`openTrailers ${!Object.keys(selected).length == 0 ? 'animate' +selected.mal_id : ""}`}  onClick={(e) => showOverlay(e)} type="button" ><span>TRAILERS</span></div>
            <div className="buttonWrapper">
            </div>
            <style jsx>{`
                .footer {
                    width: 100%;
                    display: flex;
                    justify-content: flex-end;
                    position: absolute;
                    bottom: 0;
                    z-index: 0;
                }


                .buttonWrapper {
                    background-color: transparent;
                    min-height: 150px;
                    flex: 0 0 30%;
                }

                .openTrailers {
                    background-color: ${!Object.keys(selected).length == 0 ? '#fd6568' : '#C3C3C3'};
                    height: 75px;
                    flex: 0 0 50%;
                    position:relative;
                    left: 25%;
                    top: 37.5px;
                    display:flex;
                    align-items: center;
                    justify-content: left
                    max-width: 300px;
                }

                .${'animate' +selected.mal_id} {
                    animation: animate .5s forwards;
                }

                span {
                    font-family: council, sans-serif;
                    font-weight: 400;
                    font-style: normal;
                    font-size: 2em;
                    color: black;
                    margin-left 30px;
                }

                @keyframes animate {
                    0% {
                        -webkit-clip-path: polygon(0 0, 49% 0, 100% 0, 100% 45%, 100% 100%, 48% 100%, 0 100%);
                        clip-path: polygon(0 0, 49% 0, 100% 0, 100% 45%, 100% 100%, 48% 100%, 0 100%);
                    }
                    100% {
                        -webkit-clip-path: polygon(0 5%, 60% 20%, 60% 0%, 95% 56%, 59% 100%, 60% 80%, 0 75%);
;
                        clip-path: polygon(0 5%, 60% 20%, 60% 0%, 95% 56%, 59% 100%, 60% 80%, 0 75%);
;
                    }
                }


            `}
            </style>
        </div>
    )
}

export default Footer
